//https://leetcode.com/problems/distinct-subsequences
class Solution {
public:
    int helper(int index_s, int index_t, string &s, string &t, unordered_map<int, unordered_map<int, int>> &m){
        if(index_t == t.size())
            return 1;
        if(index_s == s.size()){
            return index_t == t.size()? 1:0;
        }
        
        if(m.count(index_s) && m[index_s].count(index_t))
            return m[index_s][index_t];

        int val = 0;
        if(s[index_s] == t[index_t]){
            val += helper(index_s + 1, index_t + 1, s, t, m);
        }
        val += helper(index_s + 1, index_t, s, t, m);
        m[index_s][index_t] = val;
        return val;
    }
    int numDistinct(string s, string t) {
        if(t.size() > s.size())
            return 0;
        if(s.size() == t.size())
            return s==t? 1:0;
        unordered_map<char, vector<int>> m_s;
        for(int i=0; i<s.size(); i++){
            m_s[s[i]].push_back(i);
        }

        for(int i=0; i<t.size(); i++){
            if(!m_s.count(t[i]))
                return 0;
        }
        unordered_map<int, unordered_map<int, int>> m;//index_s, index_t, val
        return helper(0, 0, s, t, m);
    }
};
