//https://leetcode.com/problems/minimum-cost-to-connect-sticks
class Solution {
public:
    int connectSticks(vector<int>& sticks) {
        priority_queue<int, vector<int>, greater<int>> pq(sticks.begin(), sticks.end());
        int total = 0;
        while(pq.size() > 1){
            int top1 = pq.top();
            pq.pop();
            int top2 = pq.top();
            pq.pop();
            int sum = top1 + top2;
            total += sum;
            pq.push(sum);
        }
        return total;
    }
};

