//https://leetcode.com/problems/nearest-exit-from-entrance-in-maze
class Solution {
public:
    int nearestExit(vector<vector<char>>& maze, vector<int>& entrance) {
        int row = maze.size();
        int col = maze[0].size();
        int count = 0;
        queue<pair<int, int>> q;
        pair<int, int> ent;
        ent.first = entrance[0];
        ent.second = entrance[1];
        q.push({entrance[0], entrance[1]});

        int dx[] = {0, 0, -1, 1};
        int dy[] = {-1, 1, 0, 0};

        while(!q.empty()){
            int size = q.size();
            while(size--){
                pair<int, int> p = q.front();
                int x = p.first;
                int y = p.second;
                q.pop();
                if((x == 0 || x == row-1 || y == 0 || y == col-1) && (ent != p)){
                    return count;
                }
                for(int i=0; i<4; i++){
                    int r = x + dx[i];
                    int c = y + dy[i];
                    if(r >= 0 && r < row && c >= 0 && c < col && maze[r][c] == '.'){
                        q.push({r, c});
                        maze[r][c] = '-';
                    }
                }
            }
            count++;
        }

        return -1;
    }
};

