//https://leetcode.com/problems/valid-parentheses
class Solution {
public:
    bool isValid(string s) {
        stack<char> _stack;
        for(auto ch: s){
            if(ch == '(' || ch == '{' || ch == '['){
                _stack.push(ch);
            }else{
                if(_stack.empty())
                    return false;
                if(ch == ')' && _stack.top() != '(')
                    return false;
                if(ch == '}' && _stack.top() != '{')
                    return false;
                if(ch == ']' && _stack.top() != '[')
                    return false;
                _stack.pop();
            }
        }
        return _stack.empty();
    }
};

