// https://leetcode.com/problems/equal-row-and-column-pairs
class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        map<vector<int>, int> m_row;
        for(auto r: grid){
            m_row[r]++;
        }
        int count = 0;
        for(int c=0; c<grid[0].size(); c++){
            vector<int> v;
            for(int r=0; r<grid.size(); r++){
                v.push_back(grid[r][c]);
            }
            if(m_row.count(v)){
                count += m_row[v];
            }
        }

        return count;
    }
};

