class Solution {
public:
    bool increasingTriplet(vector<int>& nums) {
        int first = INT_MAX;
        int second = INT_MAX;
        for(auto n: nums){
            if(first >= n){
                first = n;
            }else if(second >= n){
                second = n;
            }else{
                return true;
            }
        }
        return false;
    }
};

