//https://leetcode.com/problems/longest-palindromic-substring
class Solution {
public:
    string longestPalindrome(string s) {
        queue<tuple<int, int, int>> q;
        pair<int, int> p = {0, 0};
        int max_val = 1;
        for(int i=0; i<s.size();i++){
            int val = 1;
            bool flag = false;
            for(int j=i+1; j<s.size(); j++){
                flag = true;
                if(s[i] == s[j]){
                    val++;
                    continue;
                }
                if(val > max_val){
                    max_val = val;
                    p = {i, j-1};
                }
                q.push({i, j-1, val});
                i = j-1;
                break;
            }
            if(!flag){
                q.push({i, i, val});
            }else{
                q.push({i, s.size()-1, val});
                if(val > max_val){
                    max_val = val;
                    p = {i, s.size()-1};
                }
            }
        }
        while(!q.empty()){
            auto [start, end, val] = q.front();
            q.pop();
            if(start-1 >= 0 && end+1 < s.size() && s[start-1] == s[end+1]){
                q.push({start-1, end+1, val+2});
                if(val+2 > max_val){
                    max_val = val+2;
                    p = {start-1, end+1};
                }
            }
        }
        string str = s.substr(p.first, p.second-p.first+1);
        return str;
    }
};
