//https://leetcode.com/problems/coin-change-ii
class Solution {
public:
    int change(int amount, vector<int>& coins) {
        sort(coins.begin(), coins.end());
        vector<unsigned long long> v(amount+1, 0);
        v[0] = 1;
        for(int c=0; c<coins.size(); c++){
            for(int a=coins[c]; a<=amount; a++){
                v[a] += v[a-coins[c]];
            }
        }
        return v.back();
    }
};

