//https://leetcode.com/problems/01-matrix
class Solution {
public:
    vector<vector<int>> updateMatrix(vector<vector<int>>& mat) {
        queue<pair<int, int>> q;
        vector<vector<int>> res(mat.size(), vector<int>(mat[0].size(), INT_MAX));
        for(int r=0; r<mat.size(); r++){
            for(int c=0; c<mat[r].size(); c++){
                if(mat[r][c] == 0){
                    q.push({r,c});
                    res[r][c] = 0;
                }
            }
        }
        int dx[4] = {0, 0, 1, -1};
        int dy[4] = {1, -1, 0, 0};
        while(!q.empty()){
            auto [r, c] = q.front();
            q.pop();
            for(int i=0; i<4; i++){
                int x = dx[i] + r;
                int y = dy[i] + c;
                if(x >= 0 && y >= 0 && x < mat.size() && y < mat[0].size() && res[x][y] > 1+res[r][c]){
                    res[x][y] = 1 + res[r][c];
                    q.push({x,y});
                }
            }
        }
        return res;
    }
};
