//https://leetcode.com/problems/permutation-in-string
class Solution {
public:
    bool checkInclusion(string s1, string s2) {
        if(s1 == s2)
            return true;
        if(s2.size() < s1.size())
            return false;
        unordered_map<char, int> m;
        for(auto ch: s1){
            m[ch]++;
        }
        unordered_map<char, int> window;
        int count = 0;
        int start = 0;
        int end = 0;
        while(end < s2.size()){
            if(m.count(s2[end])){
                window[s2[end]]++;
                if(window[s2[end]] == m[s2[end]]){
                    count++;
                }
                while(start < end && window[s2[end]] > m[s2[end]]){
                    if(window[s2[start]] == m[s2[start]])
                        count--;
                    window[s2[start]]--;
                    start++;
                }
                if(count == m.size()){
                    return true;
                }
            }else{
                start = end+1;
                window.clear();
                count = 0;
            }
            end++;
        }
        return false;
    }
};

