//https://leetcode.com/problems/task-scheduler
class Solution {
public:
    int leastInterval(vector<char>& tasks, int n) {
        vector<int> vFreq(26, 0);
        for(auto t: tasks){
            vFreq[t-'A']++;
        }
        sort(vFreq.begin(), vFreq.end());

        int maxFreq = vFreq[25];

        int idle_time = (maxFreq - 1)*n;

        for(int i=24; i>=0 && idle_time > 0; i--){
            idle_time -= min(maxFreq - 1, vFreq[i]);
        }

        idle_time = max(0, idle_time);

        return idle_time + tasks.size();
    }
};

