//https://leetcode.com/problems/maximum-distance-in-arrays
class Solution {
public:
    int maxDistance(vector<vector<int>>& arrays) {
        priority_queue<pair<int, int>> pq_max;
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq_min;

        for(int i=0; i<arrays.size(); i++){
            pq_max.push({arrays[i].back(), i});
            pq_min.push({arrays[i][0], i});
        }
        
        if(pq_max.top().second == pq_min.top().second){
            pair<int, int> p_max = pq_max.top();
            pq_max.pop();

            pair<int, int> p_min = pq_min.top();
            pq_min.pop();

            if(pq_max.top().first == p_max.first){
                pq_min.push(p_min);
            }else if(pq_min.top().first == p_min.first){
                pq_max.push(p_max);
            }else{
                if(abs(pq_max.top().first - p_min.first) > abs(pq_min.top().first - p_max.first)){
                    pq_min.push(p_min);
                }else{
                    pq_max.push(p_max);
                }
            }
        }
        
        int max_val = pq_max.top().first;
        int min_val = pq_min.top().first;

        return abs(max_val - min_val);
    }
};
